import React, { useState } from 'react';
import './App.css';

function App() {
  const [notes, setNotes] = useState([]);
  const [newNoteText, setNewNoteText] = useState('');

  const addNote = (event) => {
    event.preventDefault(); // Предотвращаем перезагрузку страницы по умолчанию

    if (newNoteText.trim()) {
      const newNote = {
        id: Date.now(),
        text: newNoteText,
        completed: false,
      };

      setNotes([...notes, newNote]);
      setNewNoteText('');
    }
  };

  const toggleNoteCompletion = (id) => {
    setNotes(
      notes.map((note) => {
        if (note.id === id) {
          return { ...note, completed: !note.completed };
        }
        return note;
      })
    );
  };

  const deleteNote = (id) => {
    setNotes(notes.filter((note) => note.id !== id));
  };

  return (
    <>
      <h1>Notes</h1>
      <form className="add-note-form" onSubmit={addNote}> {/* Добавляем обработчик события onSubmit */}
        <textarea
          value={newNoteText}
          onChange={(e) => setNewNoteText(e.target.value)}
          placeholder="write here"
        />
        <button type="submit">Add new note</button> {/* Используем type="submit" для кнопки */}
      </form>
      <ul className="notes-list">
        {notes.map((note) => (
          <li key={note.id} className="note">
            <input
              type="checkbox"
              className="note-checkbox"
              checked={note.completed}
              onChange={() => toggleNoteCompletion(note.id)}
            />
            <span className="note-text">{note.text}</span>
            <button onClick={() => deleteNote(note.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </>
  );
}

export default App;
